body {
		width: 100%;
		max-width: 100vw;
		min-width: 350px;
		height: auto;
		min-height: 100vh;
	}

	body.has-brand #wrapper,
	body div#wrapper {
		padding: 0 !important;
		box-sizing: border-box;
		width: 100%;
		margin: 0;
		overflow: hidden;
	}
	body div#wrapper > #main {
		padding: 0 !important;
		padding-bottom: 0 !important;
		box-sizing: border-box;
		width: 100%;
	}
	.b-newest_slider {
		width: 100%;
		max-width: 1200px;
		padding: 10px 15px;
		box-sizing: border-box;
		overflow: visible;
	}
	.b-newest_slider .cntrl.prev {
		left: 5px;
		width: 50px;
		top: calc(50% - 25px);
		bottom: calc(50% - 25px);
		height: 50px;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		box-sizing: border-box;
		background-color: #bcaaa4;
		box-shadow: 1px 1px 2px 0px #6f6f6f;
		border-radius: 50%;
	}
	.b-newest_slider .cntrl.prev:before {
		box-sizing: border-box;
		padding: 0;
		margin: 0;
		float: none;
		width: 2px;
		height: 10px;
		border: none;
		background-color: #ffffff;
		transform: rotate(50deg);
		margin-bottom: -5px;
	}
	.b-newest_slider .cntrl.prev:after {
		content: ' ';
		box-sizing: border-box;
		padding: 0;
		margin: 0;
		float: none;
		width: 2px;
		height: 10px;
		border: none;
		background-color: #ffffff;
		transform: rotate(-55deg);
		margin-top: 0px;
	}
	.b-newest_slider .cntrl.next {
		right: 5px;
		width: 50px;
		top: calc(50% - 25px);
		bottom: calc(50% - 25px);
		height: 50px;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		box-sizing: border-box;
		background-color: #bcaaa4;
		box-shadow: 1px 1px 2px 0px #6f6f6f;
		border-radius: 50%;
	}
	.b-newest_slider .cntrl.next:before {
		box-sizing: border-box;
		padding: 0;
		margin: 0;
		float: none;
		width: 2px;
		height: 10px;
		border: none;
		background-color: #ffffff;
		transform: rotate(-50deg);
		margin-bottom: -5px;
	}
	.b-newest_slider .cntrl.next:after {
		content: ' ';
		box-sizing: border-box;
		padding: 0;
		margin: 0;
		float: none;
		width: 2px;
		height: 10px;
		border: none;
		background-color: #ffffff;
		transform: rotate(55deg);
		margin-top: 0px;
	}
	.b-newest_slider__wrapper {
		box-sizing: border-box;
		padding: 0;
	}
	.b-newest_slider__inner {
		box-sizing: border-box;
		margin: 0;
		padding: 0;
	}
	.b-newest_slider__title {
		width: 100%;
		max-width: 1200px;
		padding: 10px 15px;
		box-sizing: border-box;
		text-align: center;
		text-transform: uppercase;
		margin-bottom: 20px;
	}
	.b-newest_slider__list {
		display: flex;
	}
	.b-newest_slider__list .b-content__inline_item {
		min-width: 120px;
		max-width: 155px;
		margin-left: 12px;
		box-sizing: border-box;
	}
	.glory {
		width: calc(100vh - 30px);
		display: flex;
		margin: 20px auto;
		height: auto;
		position: relative;
	}
	.glory img {
		width: 100%;
		max-width: 1200px;
		height: auto;
		max-height: 100%;
		display: block;
		margin: 0 auto;
		border-radius: 15px;
		box-shadow: 0px 0px 10px -5px #000000;
	}
	@keyframes pulse {
		from {
			-webkit-transform: scale3d(1, 1, 1);
			transform: scale3d(1, 1, 1);
		}
		50% {
			-webkit-transform: scale3d(1.03, 1.03, 1.03);
			transform: scale3d(1.03, 1.03, 1.03);
		}
		to {
			-webkit-transform: scale3d(1, 1, 1);
			transform: scale3d(1, 1, 1);
		}
	}
	.glory img {
		-webkit-animation-duration: 3s;
		animation-duration: 3s;
		-webkit-animation-fill-mode: both;
		animation-fill-mode: both;
		-webkit-animation-name: pulse;
		animation-name: pulse;
		-webkit-animation-timing-function: ease-in-out;
		animation-timing-function: ease-in-out;
		animation-iteration-count: infinite;
	}
	.b-content__main_filters {
		width: 100%;
		max-width: 1200px;
		padding: 0;
		box-sizing: border-box;
		display: flex;
		flex-direction: row;
		justify-content: center;
		align-items: center;
		margin: 50px auto;
		margin-bottom: 0;
		overflow: unset;
		gap: 15px;
		flex-wrap: wrap;
	}
	..b-content__main_filters_item_left {
		float: none;
	}
	.b-content__main_filters_link,
	.b-content__filters_types {
		text-decoration: none;
		background-color: transparent;
		height: auto;
		border: none;
		border-radius: 2px;
		box-shadow: 0px 0px 2px -2px #545454;
		color: #282829 !important;
		font-weight: 600;
		text-transform: lowercase;
		transition: 200ms;
		box-sizing: border-box;
	}

	.b-content__main_filters_link .filter-link,
	.b-content__filters_types .filter-link {
		padding: 0;
		margin: 0;
		box-sizing: border-box;
		line-height: 1;
	}

	.b-content__main_filters_link:hover,
	.b-content__filters_types .filter-link:hover {
		text-decoration: none;
		background-color: #efebe9;
		height: auto;
		border: none;
		border-radius: 2px;
		box-shadow: 0px 6px 6px -8px #545454;
		color: #282829 !important;
		font-weight: 600;
		transition: 200ms;
		box-sizing: border-box;
	}
	.b-content__main_filters_link.active,
	.b-content__filters_types .filter-link.active {
		background-color: #ffca28;
		height: auto;
		padding: 4px 10px;
		line-height: 1;
		border: none;
		border-radius: 2px;
		box-shadow: 2px 2px 4px -2px #545454;
		color: #282829 !important;
		font-weight: 600;
		transition: 200ms;
		text-transform: lowercase;
	}
	.b-content__filters_types {
		box-sizing: border-box;
		display: flex;
		flex-direction: row;
		justify-content: center;
		align-items: center;
		overflow: unset;
		gap: 15px;
		background-color: #fff;
		padding: 5px 20px;
		border-radius: 4px
	}
	.b-content__filters_types .filter-text {
		display: none;
	}
	.b-wrapper {
		width: 100%;
		max-width: 1200px;
		padding: 0 15px;
		box-sizing: border-box;
	}
	.b-content.b-wrapper {
		display: flex;
		flex-direction: column;
		justify-content: flex-start;
		align-items: flex-start;
		margin: 0 auto;
		box-sizing: border-box;
	}
	.b-content__inline {
		width: 100%;
	}
	.b-content__inline_inner {
		margin: 0;
	}
	.b-content__inline_inner.b-content__inline_inner_mainprobar,
	.b-content__inline_inner.b-content__inline_inner_main {
		display: flex;
		flex-direction: column;
		padding-right: 0;
		box-sizing: border-box;
	}
	.b-content__inline_inner_mainprobar .b-content__inline_item {
		margin: 0 !important;
	}
	.b-navigation {
		position: absolute;
		bottom: 0;
		box-sizing: border-box;
		width: 100%;
		display: flex;
		justify-content: center;
		align-items: center;
		gap: 5px;
		margin: 0;
		padding: 30px 0;
	}
	.b-content__inline_items {
		width: 100%;
		order: 2;
		float: none;
		display: flex;
		flex-direction: row;
		flex-wrap: wrap;
		align-items: stretch;
		justify-content: space-around;
		padding: 0;
		gap: 20px;
		margin-top: 0px;
		position: relative;
		padding-bottom: 120px;
		box-sizing: border-box;
	}
	.b-navigation span,
	.b-navigation a {
		height: 20px;
		width: 20px;
		border-radius: 4px;
		display: flex;
		justify-content: center;
		align-items: center;
		text-align: center;
		font-size: 14px;
		font-weight: 600;
		color: #444444;
	}
	.b-navigation span.no-page {
		background-color: transparent;
	}
	.b-content__inline_item {
		flex: 1 0 50%;
		min-width: 150px;
		max-width: 215px;
		height: auto;
		margin: 0;
		background-color: #ffffff;
		border-radius: 10px;
		box-shadow: 1px 1px 8px -4px #858585;
		box-sizing: border-box;
	}
	.b-content__inline_item-cover {
		border: none;
		padding: 0;
		box-sizing: border-box;
	}
	.b-content__inline_item-cover a {
		display: block;
		width: 100%;
		height: auto;
		overflow: hidden;
	}
	.b-content__inline_item .info {
		left: 5px;
		bottom: 5px;
		color: #000000;
	}
	.b-content__inline_item-cover a img {
		height: auto;
		max-height: 100%;
		width: 100%;
		display: block;
		object-fit: fill;
		margin: 0px auto;
		box-sizing: border-box;
	}
	.b-content__inline_item-cover a,
	.b-content__inline_item-cover .layer {
		background-color: #313131;
	}
	.b-content__inline_item-link {
		display: flex;
		flex-direction: column;
		align-items: flex-start;
		justify-content: flex-start;
		height: auto;
		width: 100%;
		padding: 15px 10px;
		line-height: 1.4;
		gap: 10px;
		box-sizing: border-box;
	}
	.b-content__inline_item-link > a {
		display: block;
		order: 2;
		width: 90%;
	}
	.b-content__inline_sidebar {
		width: 100%;
		background-color: #fefefe;
		padding: 10px;
		order: 1;
		float: none;
		display: flex;
		flex-wrap: wrap;
		justify-content: space-around;
		align-items: flex-start;
		margin: 10px auto;
		overflow: hidden;
		border-radius: 10px;
		box-shadow: 1px 1px 8px -4px #858585;
		gap: 15px;
		box-sizing: border-box;
		margin-bottom: 50px;
	}
	.b-seriesupdate__block {
		float: unset;
		width: calc(50% - 15px);
		box-sizing: border-box;
		overflow: hidden;
		display: none;
		border: none;
		background-color: #ffffff;
		transition: 200ms;
	}
	.b-seriesupdate__block:nth-of-type(2),
	.b-seriesupdate__block:nth-of-type(3) {
		display: block;
	}
	.b-seriesupdate__block_date {
		background-color: transparent;
		color: #000000;
		padding: 8px 14px;
		font-size: 20px;
	}
	.b-seriesupdate__block_list {
		height: 300px;
		position: relative;
		border-top: none;
		padding-bottom: 0;
		display: flex;
		flex-direction: row;
		justify-content: flex-start;
		flex-wrap: wrap;
		align-items: center;
		transition: 200ms;
		gap: 8px 15px;
		overflow-x: auto;
	}
	.b-seriesupdate__block_list_item {
		background-color: transparent;
		width: 100%;
		height: auto;
		white-space: nowrap;
		box-sizing: border-box;
		border-bottom: none;
	}
	.b-seriesupdate__block_list_item_inner {
		display: flex;
		flex-direction: row;
		justify-content: flex-start;
		align-items: flex-start;
		width: 100%;
		height: auto;
		box-sizing: border-box;
		margin: 0;
		border: none;
		background-color: #efebe9;
		border-radius: 2px;
		padding: 8px 14px;
		position: relative;
		min-width: 120px;
	}
	.b-seriesupdate__block_list_item_inner:hover {
		background-color: #fffae3;
		transition: 200ms;
	}
	.b-seriesupdate__block_list_item_inner .cell,
	.b-seriesupdate__block_list_item_inner a {
		box-sizing: border-box;
		padding: 0;
		margin: 0;
		overflow: hidden;
		white-space: nowrap;
		text-overflow: ellipsis;
	}
	.b-seriesupdate__block_list_item_inner .cell.cell-1 {
		font-size: 14px;
		text-align: left;
		padding: 0 0 2px 0;
		overflow: hidden;
		white-space: nowrap;
		text-overflow: ellipsis;
		width: 100%;
		max-width: calc(100% - 200px);
		min-width: 300px;
	}
	.b-seriesupdate__block_list_item_inner .cell.cell-1 .b-seriesupdate__block_list_link {
		color: #3e2723;
		letter-spacing: -0.1px;
		overflow: hidden;
		white-space: nowrap;
		text-overflow: ellipsis;
		display: block;
		width: 100%;
	}
	.b-seriesupdate__block_list_item_inner .cell.cell-1 .season {
		position: absolute;
		top: 5px;
		right: 10px;
		font-size: 11px;
		font-weight: 100;
		color: #777777;
		background-color: #efefef;
		padding: 5px 5px;
		box-sizing: border-box;
		border-radius: 5px;
		border: 1px solid #dddddd;
		width: 80px;
		overflow: hidden;
		text-overflow: ellipsis;
		display: block;
		text-align: center;
	}
	.b-seriesupdate__block_list_item_inner .cell.cell-2 {
		position: absolute;
		top: 4px;
		right: 100px;
		font-size: 12px;
		font-weight: 600;
		color: #777777;
		background-color: #efefef;
		padding: 5px 10px;
		box-sizing: border-box;
		border-radius: 5px;
		border: 1px solid #dddddd;
		width: 80px;
		overflow: hidden;
		text-overflow: ellipsis;
		display: block;
		text-align: center;
	}
	.b-seriesupdate__block_list_item_inner .cell.cell-2 i {
		display: none;
	}
	.b-content .b-content__htitle,
	.b-content__inline_sidebar .b-content__htitle.small {
		display: none;
	}
	.b-content__columns {
		margin: 0;
		padding: 0;
		box-sizing: border-box;
	}
	.b-player__holder_cdn,
	.b-player__container_cdn {
		max-width: 1100px;
		margin: 0 auto;
		width: 90% !important;
		height: auto !important;
		border-radius: 15px;
		overflow: hidden;
		aspect-ratio: 16 / 9;
	}
	.b-content__columns.pdt .b-content__main {
		width: 100%;
		max-width: 1200px;
		display: flex;
		flex-direction: column;
		flex-wrap: nowrap;
		justify-content: flex-start;
		align-items: stretch;
	}
	.b-post__title {
		clear: both;
	}
	.b-translators__block {
		max-width: 1200px;
		width: 100%;
		padding: 0;
		margin: 0;
		box-sizing: border-box;
	}
	.b-translators__block,
	.b-post__lastepisodeout {
		max-width: 1200px;
		width: 100%;
		background-color: #efebe9;
	}
	.b-translators__title,
	.b-translators__list {
		width: 100%;
		max-width: 1100px;
		margin: 0 auto;
	}
	.b-translators__list li {
		border-radius: 6px;
	}
	.b-translators__block .b-translators__title,
	.b-post__lastepisodeout h2 {
		color: #777777;
	}
	.b-post__title {
		box-sizing: border-box;
	}
	.b-post__origtitle {
		box-sizing: border-box;
		margin: 10px 0 5px 0;
	}
	.b-post__infolast {
		box-sizing: border-box;
	}
	.b-post__infotable {
		box-sizing: border-box;
		width: 100%;
		max-width: 1200px;
		padding: 0;
		margin: 0;
		display: flex;
	}
	.clearfix:before,
	.clearfix:after {
		content: none;
	}
	.b-post__infotable .b-post__infotable_left {
		padding: 0;
		margin: 0;
		width: 200px;
		float: none;
	}
	.b-post__infotable .b-post__infotable_right {
		padding: 0;
		margin: 0;
		width: calc(100% - 300px);
		float: none;
	}

	.b-post__description {
		width: 100%;
		max-width: 1200px;
		height: auto;
		overflow: hidden;
		background-color: #ffffff;
		padding: 15px;
		box-sizing: border-box;
	}
	.b-post__description .b-post__description_title {
		width: 100%;
		margin-bottom: 15px;
	}
	.b-post__description .b-post__description_text {
		display: block;
		width: 100%;
		line-height: 1.5;
		padding: 0;
		overflow: hidden;
		transition: 100ms;
	}
	.b-player {
		background-color: #efebe9;
	}
	.b-simple_seasons__list,
	.b-simple_episodes__list {
		width: 100%;
		max-width: 1100px;
	}
	.b-simple_seasons__list li,
	.b-simple_episodes__list li {
		border-radius: 6px;
	}
	#hd-comments-navigation {
		position: relative;
		height: 100px;
	}
	.b-sidelist__holder {
		width: 100%;
		box-sizing: border-box;
		margin: 40px auto;
	}
	.b-sidelist__holder .b-sidelist {
		width: 100% !important;
		display: flex;
		flex-wrap: wrap;
		justify-content: flex-start;
		align-items: stretch;
		gap: 20px 15px;
		box-sizing: border-box;
		margin: 0;
		padding: 0;
	}
	.b-sidelist__holder .b-sidelist .b-content__inline_item {
		min-width: 100px;
		max-width: 125px;
		flex: 1 0 10%;
		margin: 0;
		padding: 0;
		box-sizing: border-box;
	}
	.b-post__support_holder {
		color: #000000;
	}
	#footer {
		margin: 0 !important;
		width: 100%;
		padding: 0!important;
		box-sizing: border-box;
		display: flex;
		flex-direction: column;
		justify-content: flex-start;
		align-items: flex-start;
	}
	.b-footer {
		width: 100%;
		box-sizing: border-box;
		margin: 0 !important;
		padding: 0!important;
	}
	.b-footer__inner {
		color: #ffffff;
		font-size: 18px;
		text-align: center;
		min-height: 120px;
		padding: 20px 0;
		display: flex;
		flex-direction: column-reverse;
		justify-content: flex-start;
		align-items: center;
		gap: 30px;
		box-sizing: border-box;
		width: 100%;
	}
	.b-footer__menu {
		display: flex;
		flex-direction: row;
		flex-wrap: wrap;
		justify-content: space-around;
		align-items: center;
		gap: 10px;
	}
	.b-footer__menu .b-footer__menu_item a {
		font-size: 14px;
		color: #ffffff;
		white-space: nowrap;
	}
	.b-footer__left,
	.b-footer__right {
		float: none;
		width: 100%;
	}
	.b-footer__slogan {
		box-sizing: border-box;
		margin-bottom: 20px;
		width: 100%;
	}
	.comments-form {
		overflow: hidden;
	}

