.b-newest_slider {
	width: 100%;
	max-width: 1400px;
	padding: 10px 15px;
	box-sizing: border-box;
}
.b-newest_slider__title {
	width: 100%;
	max-width: 1400px;
	padding: 10px 15px;
	box-sizing: border-box;
	text-align: center;
	text-transform: uppercase;
	margin-bottom: 20px;
}
.b-newest_slider__list .b-content__inline_item {
	width: 140px;
	margin-left: 18px;
	box-sizing: border-box;
}
.b-content__main_filters {
	width: 100%;
	max-width: 1400px;
	padding: 0;
	box-sizing: border-box;
	display: flex;
	flex-direction: row;
	justify-content: center;
	align-items: center;
	margin: 50px auto;
	margin-bottom: 0;
	overflow: unset;
	gap: 15px
}
.b-content__main_filters_link,
.b-content__filters_types {
	text-decoration: none;
	background-color: transparent;
	height: auto;
	border: none;
	border-radius: 2px;
	box-shadow: 0px 0px 2px -2px #545454;
	color: #282829 !important;
	font-weight: 600;
	transition: 200ms;
}
.b-content__main_filters_link:hover,
.b-content__filters_types .filter-link:hover {
	text-decoration: none;
	background-color: #efebe9;
	height: auto;
	border: none;
	border-radius: 2px;
	box-shadow: 0px 6px 6px -8px #545454;
	color: #282829 !important;
	font-weight: 600;
	transition: 200ms;
}
.b-content__main_filters_link.active,
.b-content__filters_types .filter-link.active {
	background-color: #ffca28;
	height: auto;
	border: none;
	border-radius: 2px;
	box-shadow: 2px 2px 4px -2px #545454;
	color: #282829 !important;
	font-weight: 600;
	transition: 200ms;
}
.b-content__filters_types {
	margin-left: auto;
	box-sizing: border-box;
	display: flex;
	flex-direction: row;
	justify-content: center;
	align-items: center;
	overflow: unset;
	gap: 15px
}
.b-content__filters_types .filter-text {
	display: none;
}
.b-wrapper {
	width: 100%;
	max-width: 1400px;
	padding: 0 15px;
}
.b-content.b-wrapper {
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
	align-items: flex-start;
	margin: 0 auto;
}
.b-content__inline {
	width: 100%;
}
.b-content__inline_inner {
	margin: 0;
}
.b-content__inline_inner.b-content__inline_inner_mainprobar,
.b-content__inline_inner.b-content__inline_inner_main {
	display: flex;
	flex-direction: column;
	padding-right: 0;
	box-sizing: border-box;
}
.b-content__inline_inner_mainprobar .b-content__inline_item {
	margin: 0!important;
}
.b-content__inline_items {
	width: 100%;
	order: 2;
	float: none;
	display: flex;
	flex-direction: row;
	flex-wrap: wrap;
	align-items: stretch;
	justify-content: space-around;
	padding: 0;
	gap: 20px;
	margin-top: 0px;
	position: relative;
	padding-bottom: 100px;
	box-sizing: border-box;
}
.b-navigation {
	position: absolute;
	bottom: 0;
	box-sizing: border-box;
}
.b-content__inline_item {
	flex: 1 0 50%;
	min-width: 150px;
	max-width: 215px;
	height: auto;
	margin: 0;
	background-color: #fff;
	border-radius: 10px;
	box-shadow: 1px 1px 8px -4px #858585;
	box-sizing: border-box;
}
.b-content__inline_item-cover {
	border: none;
	padding: 0;
	box-sizing: border-box;
}
.b-content__inline_item-cover a {
	display: block;
	width: 100%;
	height: auto;
	overflow: hidden;
}
.b-content__inline_item-cover a img {
	height: auto;
	max-height: 100%;
	width: 100%;
	display: block;
	object-fit: fill;
	margin: 0px auto;
	box-sizing: border-box;
}
.b-content__inline_item-cover a,
.b-content__inline_item-cover .layer {
	background-color: #313131;
}
.b-content__inline_item-link {
	display: flex;
	flex-direction: column;
	align-items: flex-start;
	justify-content: flex-start;
	height: auto;
	width: 100%;
	padding: 15px 10px;
	line-height: 1.4;
	gap: 10px;
	box-sizing: border-box;
}
.b-content__inline_item-link > a {
	display: block;
	order: 2;
	width: 90%;
}
.b-content__inline_sidebar {
	width: 100%;
	background-color: #fefefe;
	padding: 10px;
	order: 1;
	float: none;
	display: flex;
	flex-wrap: wrap;
	justify-content: space-around;
	align-items: flex-start;
	margin: 10px auto;
	overflow: hidden;
	border-radius: 10px;
	box-shadow: 1px 1px 8px -4px #858585;
	gap: 15px;
	box-sizing: border-box;
	margin-bottom: 50px;
}
.b-seriesupdate__block {
	float: unset;
	width: calc(50% - 15px);
	box-sizing: border-box;
	overflow: hidden;
	display: none;
	border: none;
	background-color: #fff;
	transition: 200ms;
}
.b-seriesupdate__block:nth-of-type(2),
.b-seriesupdate__block:nth-of-type(3) {
	display: block;
}
.b-seriesupdate__block_date {
	background-color: transparent;
	color: #000;
	padding: 8px 14px;
	font-size: 20px;
}
.b-seriesupdate__block_list {
	height: 300px;
	position: relative;
	border-top: none;
	padding-bottom: 0;
	display: flex;
	flex-direction: row;
	justify-content: flex-start;
	flex-wrap: wrap;
	align-items: center;
	transition: 200ms;
	gap: 8px 15px;
	overflow-x: auto;
}
.b-seriesupdate__block_list_item {
	background-color: transparent;
	width: 100%;
	height: auto;
	white-space: nowrap;
	box-sizing: border-box;
	border-bottom: none;
}
.b-seriesupdate__block_list_item_inner {
	display: flex;
	flex-direction: row;
	justify-content: flex-start;
	align-items: flex-start;
	width: 100%;
	height: auto;
	box-sizing: border-box;
	margin: 0;
	border: none;
	background-color: #efebe9;
	border-radius: 2px;
	padding: 8px 14px;
	position: relative;
	min-width: 120px;
}
.b-seriesupdate__block_list_item_inner:hover {
	background-color: #fffae3;
	transition: 200ms;
}
.b-seriesupdate__block_list_item_inner .cell,
.b-seriesupdate__block_list_item_inner a {
	box-sizing: border-box;
	padding: 0;
	margin: 0;
	white-space: nowrap;
}
.b-seriesupdate__block_list_item_inner .cell.cell-1 {
	font-size: 14px;
	text-align: left;
	padding: 0 0 2px 0;
}
.b-seriesupdate__block_list_item_inner .cell.cell-1 .b-seriesupdate__block_list_link {
	color: #3e2723;
	letter-spacing: -0.1px;
}
.b-seriesupdate__block_list_item_inner .cell.cell-1 .season {
	position: absolute;
	top: 10px;
	right: 14px;
	font-size: 11px;
	font-weight: 100;
	color: #777;
}
.b-seriesupdate__block_list_item_inner .cell.cell-2 {
	position: absolute;
	top: 10px;
	right: 120px;
	font-size: 12px;
	font-weight: 600;
	color: #777;
}
.b-seriesupdate__block_list_item_inner .cell.cell-2 i {
	display: none;
}
.b-content__inline_sidebar .b-content__htitle.small {
	display: none;
}
.b-content__columns {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}
.b-player__holder_cdn,
.b-player__container_cdn {
	max-width: 1100px;
	width: auto!important;
	height: auto!important;
	border-radius: 15px;
	overflow: hidden;
	aspect-ratio: 16 / 9;
}
.b-content__columns.pdt .b-content__main {
	display: flex;
	flex-direction: column;
	flex-wrap: nowrap;
	justify-content: flex-start;
	align-items: stretch;
}
.b-post__title {
	clear: both;
}
.b-translators__block {
	clear: both;
}
.b-post__lastepisodeout {
	background-color: #efebe9;
}
.b-post__lastepisodeout h2 {
	color: #777;
}
.b-post__title {
	box-sizing: border-box;
}
.b-post__origtitle {
	box-sizing: border-box;
}
.b-post__infolast {
	box-sizing: border-box;
}
.b-post__infotable {
	box-sizing: border-box;
}
.b-post__description {
	height: auto;
	overflow: hidden;
	background-color: #fff;
	padding: 15px;
	box-sizing: border-box;
}
.b-post__description .b-post__description_title {
	width: 100%;
}
.b-post__description .b-post__description_text {
	display: block;
	width: 100%;
	line-height: 1.5;
	padding: 0;
	overflow: hidden;
	transition: 100ms;
}
.b-player {
	background-color: #efebe9;
}
.b-simple_seasons__list,
.b-simple_episodes__list {
	width: 100%;
	max-width: 1100px;
}
.b-simple_seasons__list li,
.b-simple_episodes__list li {
	border-radius: 6px;
}

